import React from 'react'
import { Container, Row, Col, Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';
import Fade from 'react-reveal/Fade';
import Slider from "react-slick";


const items = [
    {
      src: "images/homepage/servies/strategy.png",
      name: 'Strategy',
      rank: '1',
      description: 'Khabib Nurmagomedov is a Russian professional . He is a two-time to be have the post'
    },
    {
      src: "images/homepage/servies/design.png",
      name: 'Design',
      rank: '2',
      description: 'Conor Anthony McGregor is an Irish retired martial artist and boxer have the post in morning'
    },
    {
      src: "images/homepage/servies/development.png",
      name: 'Development',
      rank: '3',
      description: 'Alexander Volkanovski is an Australian  martial artist, curre of the panel in world'
    },
    {
      src: "images/homepage/servies/help.png",
      name: 'Help & Supports',
      rank: '4',
      description: 'Jonathan Dwight Jones is an American professional mixed matists.'
    }
  ];

  const settingsSliderMobile = {
    arrows: false,
	dots: false,
	infinite: false,
	speed: 100,
	slidesToShow: 1.5,
	slidesToScroll: 1,
  };

const Servies = () => {
    const servicesCard = items.map((item, s) => (
        <Col xs="12" sm="12" md="6" lg="3" key={s} className=" servies-box ">
            <div className=" ">
                    <div className=" justify-content-center align-self-center" 
                    style={{ textAlign: 'center', width: '100%'}}>
                        <img style={{ width: '30%'}} src={item.src} alt="card-image"/>
                    </div>
                    <div className="text-center" style={{ padding: '1rem'}}>
                       <p style={{ fontSize: '20px', color:'#404044'}}>{item.name}</p>
                        <p style={{ color: '#8b8b92'}}>{item.description}</p>
                    </div>
            </div>
        </Col>
      ));
      const servicesCardMobile = items.map((item, s) => (
        <Col xs="12" sm="12" md="6" lg="3" key={s} className=" servies-box ">
            <Card style={{ backgroundColor: '#29bae4'}} >
            <div className=" " 
                    style={{ width: '30%', alignSelf: 'center', paddingTop: '2rem'}}>
                        <img style={{ }} src={item.src} alt="card-image"/>
                    </div>
                <CardBody>
                <div className="text-center" style={{ padding: '1rem'}}>
                       <p style={{ fontSize: '20px', color:'#ffffff'}}>{item.name}</p>
                        <p style={{ color: '#ffffff'}}>{item.description}</p>
                    </div>
                 </CardBody>
            </Card> 
        </Col>
      ));
    return (
        <div>
                <Fade bottom duration={1000} delay={600} distance="30px">

            <Row>
            <Col xs="12" sm="12" md="12" lg="12" className=" ">
                  <div className="text-center">
                      <p style={{ color: '#8b8b92'}}>Our Services</p>
                      <h2 style={{ fontSize: '2.5rem'}}>Web & Mobile app design, <br/>bring your ideas to life</h2>
                      <p style={{ color: '#8b8b92'}}>Far far away, behind the word mountains, far from the countries Vokalia
                          <br/> and Consonantia, there live the blind texts. Separated they live in</p>
                  </div>
            </Col>
            </Row>
            <Row className="d-flex justify-content-center align-self-center hide-for-mobile">
                {servicesCard}
            </Row>
            <div  className="hide-for-desktop">
                            <Slider {...settingsSliderMobile}className="">
                                {servicesCardMobile}
                            </Slider>
                        </div>
            </Fade>
        </div>
    )
}

export default Servies
