import React, {  useEffect, useState } from 'react';
import Fade from 'react-reveal/Fade';
import Tilt from 'react-tilt';

import { Container, Row, Col, Button } from 'reactstrap';


const HomeTiles = () => {
  const [isDesktop, setIsDesktop] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    if (window.innerWidth > 769) {
      setIsDesktop(true);
      setIsMobile(false);
    } else {
      setIsMobile(true);
      setIsDesktop(false);
    }
  }, []);


  return (
    <section className="home-tiles">
      <Container>
        <div className="home-tiles-wrapper">
        <Fade bottom duration={1000} delay={600} distance="30px">
          <div className="text-center">
            <p style={{ color: '#8b8b92' }}>Our Work</p>
            <h2 style={{ fontSize: '2.5rem' }}>View our works below to see our <br />
                      design and way of development.</h2>
            <p style={{ color: '#8b8b92' }}>Far far away, behind the word mountains, far from the countries Vokalia
                          <br /> and Consonantia, there live the blind texts. Separated they live in</p>
          </div>
          </Fade>
          <Row>
            <Col lg={4} sm={12} className="p-0">
            <Fade left={isDesktop} bottom={isMobile} duration={1000} delay={1000} distance="30px">                <div className="home-tiles-text justify-content-center align-self-center">
                  <h2 className="home-tiles-text-title">Project Title</h2>
                  <div>
                    <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Excepturi neque, ipsa animi maiores repellendu distinctioaperiam earum dolor voluptatum consequatur blanditiis inventore debitis fuga numquam voluptate architecto itaque molestiae.</p>
                  </div>
                  <Button
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn-transparency btn-transparency-primary"
                    // style={{ width: '20%'}} 
                    >
                    See Live
                      </Button>
                  {/* <a
                    target="_blank"
                    rel="noopener noreferrer"
                    className="cta-btn text-color-main"
                  >
                    Source Code
                        </a> */} 
                </div>
              </Fade>
            </Col>
            <Col lg={8} sm={12} className="p-0">
            <Fade bottom duration={1000} delay={600} distance="30px">
                <div className="home-tiles-image">
                  <a

                    target="_blank"
                    aria-label="Project Link"
                    rel="noopener noreferrer"
                  >
                    <Tilt
                      options={{
                        reverse: false,
                        max: 8,
                        perspective: 1000,
                        scale: 1,
                        speed: 300,
                        transition: true,
                        axis: null,
                        reset: true,
                        easing: 'cubic-bezier(.03,.98,.52,.99)',
                      }}
                    >
                      <div data-tilt className="thumbnail rounded" style={{ width: '100%' }}>
                        <img style={{ width: '100%' }} src="../images/homepage/work.jpg" alt="banner" />
                      </div>
                    </Tilt>
                  </a>
                </div>
              </Fade>
            </Col>
          </Row>

        </div>
      </Container>
    </section>
  );
};

export default HomeTiles;
