import React from 'react'
import Link from "next/link";
import { Container, Row, Col, Card,Input, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button } from 'reactstrap';


class Quantity extends React.Component {
    constructor(props) {
      super(props);

      this.state = {value: 500}
      this.increment = this.increment.bind(this);
      this.decrement = this.decrement.bind(this);
    }

    increment() {
      this.setState(prevState => {value: ++prevState.value});
    }

    decrement() {
      this.setState(prevState => {value: prevState.value > 0? --prevState.value : 0});
    }

    render() {

      return (
        <div>
          <p>
          Set the quantity
         </p>
        <div className="quantity-input">
          <Button className="quantity-input__modifier quantity-input__modifier--left" 
          onClick={this.decrement}>
            &mdash;
          </Button>
          <Input className="quantity-input__screen" type="text" value={this.state.value} readonly />
          <Button className="quantity-input__modifier quantity-input__modifier--right" 
          onClick={this.increment}>
            &#xff0b;
          </Button>  
        </div>  
        <style jsx>
        {`

`}
      </style>
        </div>
      );
    }
  }

  export default Quantity;
