import React, {  useState, useEffect } from 'react';
import {Button, Container, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';
import Typing from 'react-typing-animation';
import Fade from 'react-reveal/Fade';


const HeroBanner = (props) => { 
    const videoSource = "/images/homepage/home-page-banner2.jpg"
    const {
        buttonLabel,
        className
      } = props;

      const [modal, setModal] = useState(false);

      const toggle = () => setModal(!modal);

    return ( 
        <Container fluid>
            <div>
            <div className="video-conataine-main hide-for-mobile" >

                <img style={{ width: '100%'}} src={videoSource} alt="hh"/>
                <Fade bottom duration={1000} delay={600} distance="30px">

                <div className="content-main">                    
                    <div className="subContent-inner" >
                        <h1 className="text-filter" style={{ fontSize: '4rem', color: '#ffffff'}}>We win the tough fights</h1>
                        <p style={{ fontSize: '2rem', color: '#ffffff'}}>Provide meals to the young kids</p>                   
                         <Button 

                         className="btn-transparency btn-transparency-primary"
                         style={{ width:'50%', marginTop: '2rem'}}
                         >know more</Button>
                    </div>

                </div>
                {/* <div className="homepage-cloude" > */}
                    {/* <img style={{ width: '100%'}} src="/images/homepage/cloud.png" alt="hh"/> */}
                    {/* </div> */}
                </Fade>
           </div>
            <div className="hide-for-desktop" style={{ width: '100%'}}>
            <Fade bottom duration={1000} delay={600} distance="30px">  
                {/* <img
                    src="/images/homepage/hero-background-mobile.jpg"
                    alt=""
                    style={{ width: '100%' }}
                /> */}


                 <div className="hero-banner-image-mobile">
                   <div style={{ paddingLeft: '1rem', paddingRight: '1rem', paddingTop: '3rem', paddingBottom: '3rem'}}>
                   <h1 className="text-center" style={{ fontSize: '3rem', color: '#fff'}}>We win the tough fights.</h1>
                        <p className="text-center" style={{ fontSize: '2rem', color: '#ffffff'}}>Provide meals to the young kids</p>                   

                   </div>

                </div>

                </Fade>
           </div>
        </div>

            <style global jsx>{`
            .text-filter{
                filter: drop-shadow(2px 4px 6px black);
            }


            }
             .hero-banner-image-mobile{
                overflow: hidden;
                height: auto;
                box-shadow: none;
                border: none;
                background: #1F1F1F url(/images/homepage/hero-background-mobile.jpg) no-repeat center center;
                background-size: cover !important;
               -webkit-background-size: cover !important;
                background-attachment: fixed;           
                position: relative;
                min-height: 200px;
                width: 100%;              
            }

            .homepage-cloude{
                // position: absolute;
                z-index: 1;
                bottom: 0;
                left: 0;
                width: 250.625em;
                height: 43.75em;
                -webkit-animation: cloudLoop 80s linear infinite;
                animation: cloudLoop 80s linear infinite;
                background-image: url("/images/homepage/cloud.png");
            }
            `}</style>

        </Container>
    )
}

export default HeroBanner
