import React from 'react';
import { Container, Row, Col, Input, FormGroup, Form, Button } from 'reactstrap';
import axios from 'axios';
import Fade from 'react-reveal/Fade';



class Newsletter extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			messageSubmitted: false,
			email: '',
		};
	}

	messageSubmitted = (event) => {
		event.preventDefault();

		// const name = document.getElementById('ns-sub-panel-name').value;
		const email = document.getElementById('ns-sub-panel-email').value;

		axios({
			method: 'post',
			url: `/api/newsletter/leadgen`,
			data: {
				email,
				source: 'homepage',

			},
		}).then((res) => {
			if (res) {
				this.setState({
					messageSubmitted: true,
					message: 'Thank you for signing up!'
				});
			}
		}).catch((err) => {
			// console.log("rajeevnayan", err)
		});

	}

	render() {
		return (
			<Container fluid className="newsletter-container-canvas">
				<Fade bottom duration={1000} delay={600} distance="30px">
					<Container className="newsletter-container">
						<div className="text-center" >
							<h1 style={{ color: 'white' }}>Subcribe to our Newsletter</h1>
							<p style={{ color: 'white' }}>Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts. Separated they live in</p>
						</div>
						<Row>
							<Col sm="12" xs="12" md="2" lg="3" />
                            <Col sm="12" xs="12" md="8" lg="6">
							<Row className="w-100">
							{!this.state.messageSubmitted ? 
                            <Form className="form-inline w-100" onSubmit={e => this.messageSubmitted(e)}>							

								<Col xs="12" lg="8" className=" newsletter-component">
									<FormGroup>
                                        <Input style={{ width: '100%' }} 
                                        required type="email" name="Email" 
                                        id="ns-sub-panel-email" 
                                        placeholder="Enter your e-mail"  />
									</FormGroup>
								</Col>

								<Col xs="12" lg="4" className=" newsletter-component">
									<FormGroup>
										<Button
											style={{
												width: '100%',
                                            }}
                                            onSubmit={e => this.messageSubmitted(e)}
											color="secondary"
											className="btn-transparency btn-transparency-primary"
										> sign up 
										</Button>
									</FormGroup>
								</Col>
							</Form>
								:
								<div className="justify-content-center" 
								style={{alignItems: 'center',maxWidth: '100%', }}	>
									<p style={{ color: '#fff', paddingTop: '1rem', fontSize: '20px'}}>
                                        {this.state.message}</p>
								</div>
							}
						</Row>
							</Col>
							<Col sm="12" xs="12" md="2" lg="3" />
						</Row>

					</Container>
					<style jsx global>
						{`
						.newsletter-component{
							padding: 8px;
						}
						@media screen and (min-width: 786px) {
							.newsletter-component{
								padding: 1rem;
							}
						}

						.newsletter-container{
							margin-top: 4rem;
							margin-bottom: 4rem;  
						 }  

						 .newsletter-container-canvas{
						   overflow: hidden;
						   height: auto;
						   box-shadow: none;
						   border: none;
						   // background:linear-gradient(270deg, #f50a81 25.28%, #9d09db 59.7%, #f722c9 97.75%);
						//    background: #ee76ad;
						   background: #1F1F1F url(/images/homepage/hero-background.jpg) no-repeat center center;
						 background-size: cover !important;
						  -webkit-background-size: cover !important;
						 background-attachment: fixed;

						 position: relative;
						 min-height: 200px;
						 width: 100%;

						 }


						  `}
					</style>		  
				</Fade>
			</Container>
		);
	}
}
export default Newsletter;
